/**

import React, { Component } from 'react';
import {connect,useDispatch} from 'react-redux';
import { TOGGLE } from '../../actions/lightswitch';
import './Room.css';

const flipLight = () => {
    console.log("fliplight Called");
    return {type:"TOGGLE"};
  
};

//callback to connect fn of "redux-react"
const mapStateToPropsRoom = (state) => {
    return { isLightOn: state.isLightOn };
};


class Room extends React.Component {  
    Action(){
        const dispatch = useDispatch();
        return dispatch(flipLight);
    }
    render() {
      const lightedness = this.props.isLightOn ? "lit" : "dark";
      
  
      return (
        <div className={`room ${lightedness}`}>
          the room is {lightedness}
          <br />
          <button onClick={()=>{
              Action()
            }}>flip</button>
        </div>
      );
    }
  }
  export default connect(null,  mapStateToPropsRoom )(Room);
