import './App.css';
import { useSelector, useDispatch } from "react-redux";
import { reset, increment } from "./redux/actions/index.js";
import './../node_modules/bootstrap/dist/css/bootstrap.css';


function App() {
  const counter = useSelector((state) => state.counter);
  const dispatch = useDispatch();

  return (
    <div className="d-flex flex-column m-5 p-5 col-4" style={{margin:"50%"}}>

      {console.log(counter)}
      <h3>
         You have walked {counter} steps today!
      </h3>
      <button className='btn btn-warning' onClick={() => dispatch(increment())}>Add a Step</button><br />
      <button className='btn btn-secondary' onClick={() => dispatch(reset())}>Reset</button>
    </div>
  );
}

export default App;
